import base64
import requests
import json
import os

# Create a tiny 1x1 black pixel JPEG in base64 for testing the plumbing
# (Though EasyOCR won't find text, it confirms the route works)
tiny_jpeg_b64 = "data:image/jpeg;base64,-ADAMBAAIRAxEAPwD3+iiigD//2Q=="

def test_ocr():
    url = "https://127.0.0.1:5000/api/ocr"
    payload = {"image": tiny_jpeg_b64}
    
    print(f"Testing OCR endpoint at {url}...")
    try:
        response = requests.post(url, json=payload, timeout=30, verify=False)
        print(f"Status: {response.status_code}")
        print(f"Response: {json.dumps(response.json(), indent=2)}")
    except Exception as e:
        print(f"Error: {e}")

if __name__ == "__main__":
    test_ocr()
